export default function (req, res) {
    require('dotenv').config()

    let nodemailer = require('nodemailer')
    const transporter = nodemailer.createTransport({
        port: 465,
        host: "premium171.web-hosting.com",
        auth: {
            user: 'test@delpinocardenas.com.ar',
            // pass: process.env.password,
            pass: "dB5pEJsayr",
        },
        secure: true,
    });

    // name: '',
    //     phone: '',
    //     email: '',
    //     address: '',
    //     description: '',

    const mailData = {
        from: 'test@delpinocardenas.com.ar',
        to: 'test@delpinocardenas.com.ar',
        subject: `Mensaje de ${req.body.name}`,
        text: req.body.phone,
        html: `<div>${req.body.description}</div>`
    }

    transporter.sendMail(mailData, function (err, info) {
        if(err)
            console.log(err)
        else
            console.log(info);
    })

    console.log(req.body)
    res.send('success')
}
